  }
  .row {
    white-space: nowrap;
    margin-bottom: 10px;
    text-align: center;
    margin-right: 0px;
    margin-left: 0px;
  }
  .screen {
    width: auto;
    padding-top: 50px;
    text-align: center;
    font-size: 16px;
    color: #666;
    background: url(data:image/png;base64,) no-repeat;
    background-position-x: center;
    position: relative;
    margin-bottom: 40px
  }
  .c-screen-line {
    width: 0px;
    border-left: 1px dashed #e5e5e5;
    position: absolute;
    top: 90px;
    bottom: 0;
    margin-left: 50%;
    text-align: center;
  }
  .seat.empty {
    background-image: none
  }

  .seat.sold {
    background-image: url(data:image/png;base64,)
  }

  .seat.selected {
    background-image: url(data:image/png;base64,)
  }
  .main {
    width: 100%;
    border: 1px solid #e5e5e5;
    font-size: 0
}
  .hall {
    width: 820px;
